import "https://deno.land/x/xhr@0.1.0/mod.ts";
import { serve } from "https://deno.land/std@0.168.0/http/server.ts";
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.7.1';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface ChallengeRequest {
  functionName: string;
  testPrompts: string[];
  modelIds: string[];
}

interface ModelTestResult {
  modelId: string;
  modelName: string;
  provider: string;
  prompt: string;
  response: string;
  score: number;
  latency: number;
  cost: number;
  metrics: {
    relevancy: number;
    accuracy: number;
    creativity: number;
    coherence: number;
  };
}

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    // Initialize Supabase client
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    const { functionName, testPrompts, modelIds }: ChallengeRequest = await req.json();

    console.log(`Running champion challenge for function: ${functionName}`);
    console.log(`Testing ${modelIds.length} models with ${testPrompts.length} prompts`);

    const results: ModelTestResult[] = [];

    // Get model configurations
    const { data: models, error: modelsError } = await supabase
      .from('ai_provider_models')
      .select(`
        id,
        model_name,
        display_name,
        ai_providers!inner(name, base_url, env_key)
      `)
      .in('id', modelIds);

    if (modelsError) {
      throw new Error(`Failed to fetch models: ${modelsError.message}`);
    }

    // Run tests for each model and prompt combination
    for (const model of models) {
      for (const prompt of testPrompts) {
        console.log(`Testing model ${model.model_name} with prompt: ${prompt.substring(0, 50)}...`);
        
        try {
          const result = await testModelWithPrompt(model, prompt, functionName);
          results.push(result);
        } catch (error) {
          console.error(`Error testing model ${model.model_name}:`, error);
          // Add a failed result
          results.push({
            modelId: model.id,
            modelName: model.model_name,
            provider: model.ai_providers.name,
            prompt,
            response: 'Error: Failed to get response',
            score: 0,
            latency: 0,
            cost: 0,
            metrics: { relevancy: 0, accuracy: 0, creativity: 0, coherence: 0 }
          });
        }
      }
    }

    console.log(`Challenge completed. Generated ${results.length} results`);

    return new Response(JSON.stringify({ 
      success: true, 
      results,
      summary: {
        totalTests: results.length,
        modelsCount: modelIds.length,
        promptsCount: testPrompts.length,
        functionName
      }
    }), {
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
    });

  } catch (error) {
    console.error('Error in champion challenge:', error);
    return new Response(JSON.stringify({ 
      error: error.message,
      success: false
    }), {
      status: 500,
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
    });
  }
});

async function testModelWithPrompt(model: any, prompt: string, functionName: string): Promise<ModelTestResult> {
  const startTime = Date.now();
  
  // Get API key for the provider
  const apiKey = Deno.env.get(model.ai_providers.env_key);
  if (!apiKey) {
    throw new Error(`API key not found for provider: ${model.ai_providers.name}`);
  }

  let response: string;
  let cost = 0;

  // Call the appropriate API based on provider
  if (model.ai_providers.name.toLowerCase().includes('openai')) {
    const result = await callOpenAI(apiKey, model.model_name, prompt);
    response = result.response;
    cost = result.cost;
  } else if (model.ai_providers.name.toLowerCase().includes('anthropic')) {
    const result = await callAnthropic(apiKey, model.model_name, prompt);
    response = result.response;
    cost = result.cost;
  } else if (model.ai_providers.name.toLowerCase().includes('google')) {
    const result = await callGoogle(apiKey, model.model_name, prompt);
    response = result.response;
    cost = result.cost;
  } else {
    throw new Error(`Unsupported provider: ${model.ai_providers.name}`);
  }

  const latency = Date.now() - startTime;

  // Evaluate the response
  const metrics = await evaluateResponse(prompt, response, functionName);
  const score = (metrics.relevancy + metrics.accuracy + metrics.creativity + metrics.coherence) / 4;

  return {
    modelId: model.id,
    modelName: model.model_name,
    provider: model.ai_providers.name,
    prompt,
    response,
    score,
    latency,
    cost,
    metrics
  };
}

async function callOpenAI(apiKey: string, model: string, prompt: string) {
  const response = await fetch('https://api.openai.com/v1/chat/completions', {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${apiKey}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      model: model,
      messages: [{ role: 'user', content: prompt }],
      max_tokens: 500,
      temperature: 0.7,
    }),
  });

  const data = await response.json();
  
  if (!response.ok) {
    throw new Error(`OpenAI API error: ${data.error?.message || 'Unknown error'}`);
  }

  return {
    response: data.choices[0].message.content,
    cost: (data.usage?.total_tokens || 0) * 0.00002 // Rough estimate
  };
}

async function callAnthropic(apiKey: string, model: string, prompt: string) {
  const response = await fetch('https://api.anthropic.com/v1/messages', {
    method: 'POST',
    headers: {
      'x-api-key': apiKey,
      'Content-Type': 'application/json',
      'anthropic-version': '2023-06-01',
    },
    body: JSON.stringify({
      model: model,
      max_tokens: 500,
      messages: [{ role: 'user', content: prompt }],
    }),
  });

  const data = await response.json();
  
  if (!response.ok) {
    throw new Error(`Anthropic API error: ${data.error?.message || 'Unknown error'}`);
  }

  return {
    response: data.content[0].text,
    cost: (data.usage?.input_tokens + data.usage?.output_tokens || 0) * 0.00002
  };
}

async function callGoogle(apiKey: string, model: string, prompt: string) {
  const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/${model}:generateContent?key=${apiKey}`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      contents: [{ parts: [{ text: prompt }] }],
      generationConfig: {
        maxOutputTokens: 500,
        temperature: 0.7,
      }
    }),
  });

  const data = await response.json();
  
  if (!response.ok) {
    throw new Error(`Google API error: ${data.error?.message || 'Unknown error'}`);
  }

  return {
    response: data.candidates[0].content.parts[0].text,
    cost: 0.00001 // Rough estimate
  };
}

